import UIKit

class LaunchRouter: LaunchWireframeProtocol {
    
    weak var viewController: UIViewController?
    
    static func createModule() -> UIViewController {
        // Change to get view from storyboard if not using progammatic UI
        let view = LaunchViewController(nibName: nil, bundle: nil)
        let interactor = LaunchInteractor()
        let router = LaunchRouter()
        let presenter = LaunchPresenter(interface: view, interactor: interactor, router: router)
        
        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view
        
        return view
    }
    
    func goToLogin(){
        let loginView = LoginRouter.createModule()
        let navigationController = UINavigationController(rootViewController: loginView)
        UIApplication.shared.windows.first?.rootViewController = navigationController
    }
    
    func goToHome(){
        let homeView = TabBarHome()
        let navigationController = UINavigationController(rootViewController: homeView)
//        self.viewController?.navigationController?.pushViewController(homeView, animated: true)
        UIApplication.shared.windows.first?.rootViewController = navigationController
    }
}
